var Proto = "ns=16;s=Presse_API_Lent_";
var Longueur = Proto.length;

// Récupération du message
var Data = msg;

// Vérification de la structure de addressSpaceItems
if (Data.addressSpaceItems && Data.addressSpaceItems.length > 0) {
    node.warn("addressSpaceItems contient " + Data.addressSpaceItems.length + " éléments.");

    // Vérification de l'existence de nodeId pour le premier élément
    if (Data.addressSpaceItems[0].nodeId) {
        var nodeIdStr = Data.addressSpaceItems[0].nodeId.toString();
        node.warn("nodeId complet : " + nodeIdStr);

        // Extraction du nom et du numéro
        var Name = nodeIdStr.substring(Longueur);
        var Numero = parseInt(nodeIdStr.substring(Longueur, Longueur + 3));

        node.warn("Nom extrait : " + Name);
        node.warn("Numéro extrait : " + Numero);

    } else {
        node.warn("Erreur : Le premier élément de addressSpaceItems n'a pas de nodeId.");
    }

} else {
    node.warn("Erreur : addressSpaceItems est vide ou inexistant.");
}

// Récupération de la valeur
var Value = Data.payload.value.value;

// Vérification de la validité de la valeur récupérée
if (Value === undefined || Value === null) {
    node.warn("Erreur : La valeur récupérée est undefined ou null.");
} else {
    node.warn("Valeur récupérée : " + Value);
}

// Récupération du format
var FormatList = global.get("varFormatList") || [];
var Format = FormatList[Numero - 1];

// Debug
node.warn("Format brut : " + Format);
var FormatNumber = parseInt(Format);
node.warn("Converti en int : " + FormatNumber);

// Formatage uniquement si le format est un nombre valide
if (!isNaN(FormatNumber)) {
    Value = parseFloat(Value).toFixed(FormatNumber).toString();
} else {
    node.warn("Format non valide ou non numérique.");
}

// Récupération de l'unité
var Unit = global.get("varUnitList")[Numero - 1] || "";

// Récupération de l'horodatage
var DateTimeStr = new Date(Data.payload.sourceTimestamp);
var DateTime = DateTimeStr.getTime();

// Création de la variable de sortie
var myvar_output = { Name, DateTime, Value, Unit };

// Envoi final
msg = {};
msg.payload = { data: myvar_output };

return msg;

// Get global flag
var bConnectLost = global.get('ConnectionLost') || false;

// Default settings
var injectMssge = {};
injectMssge.topic = "";
injectMssge.nodetype = "inject";
injectMssge.injectType = "listen";
injectMssge.addressSpaceItems = [];

var optionsParam = {
    "requestedPublishingInterval": 1000,
    "requestedLifetimeCount": 10,
    "requestedMaxKeepAliveCount": 5,
    "maxNotificationsPerPublish": 10,
    "publishingEnabled": true,
    "priority": 10,
    "clientName": "nr-iot"
};

injectMssge.payload = { "interval": 6000, "queueSize": 10, "options": optionsParam };

node.warn("subscribe msg : " + JSON.stringify(msg.varNodeIdList, null, 2));

// Récupération du dictionnaire global ou création si inexistant
var nodeIdToName = global.get('NodeIdToName') || {};

// Vérification de la validité de varNodeIdList et varNameList
if (!msg.varNodeIdList || !msg.varNameList) {
    node.warn("Les listes varNodeIdList ou varNameList sont vides ou non définies");
    return null; // On arrête ici si les listes sont invalides
}

// Cas avec une seule variable
if (!Array.isArray(msg.varNameList) && !Array.isArray(msg.varNodeIdList)) {
    var nodeId = msg.varNodeIdList;
    var name = msg.varNameList;

    if (nodeId !== "invalid") {
        injectMssge.addressSpaceItems.push({ "name": name, "nodeId": nodeId });
        nodeIdToName[nodeId] = name; // Ajout dans le dictionnaire
    } else {
        node.warn("NodeId invalide trouvé pour l'élément : " + JSON.stringify({ name, nodeId }));
    }
} else {
    // Plusieurs variables
    for (var i = 0; i < msg.varNameList.length; i++) {
        var nodeId = msg.varNodeIdList[i];
        var name = msg.varNameList[i];

        // Vérification si le NodeId est valide
        if (nodeId !== "invalid") {
            injectMssge.addressSpaceItems.push({ "name": name, "nodeId": nodeId });
            nodeIdToName[nodeId] = name; // Ajout dans le dictionnaire
        } else {
            node.warn("NodeId invalide trouvé pour l'élément : " + JSON.stringify({ name, nodeId }));
        }
    }
}

// Sauvegarde globale
global.set('NodeIdToName', nodeIdToName);

// Lancement de la subscription
if (bConnectLost === false) {
    console.log("---- FIRST SUBSCRIBE ------");
    setTimeout(SendSubscribeMessage, 5000); // ← délai ajouté
} else {
    console.log("---- CONNECT LOST - RESUBSCRIBE  ------");
    setTimeout(SendSubscribeMessage, 5000);
}


function SendSubscribeMessage() {
    node.warn("msg avant envoi dans subscribe : " + JSON.stringify(injectMssge, null, 2));

    // Vérification si les addressSpaceItems contiennent des nodeId valides
    if (!injectMssge.addressSpaceItems || injectMssge.addressSpaceItems.length === 0) {
        node.warn("Aucun nodeId valide trouvé dans addressSpaceItems");
        return null; // On arrête ici si aucun nodeId valide
    }

    // Ajouter des messages de débogage pour vérifier les nodeId
    injectMssge.addressSpaceItems.forEach(item => {
        if (!item.nodeId || item.nodeId === "invalid") {
            node.warn("NodeId invalid trouvé pour l'élément : " + JSON.stringify(item));
        } else {
            node.warn("NodeId valide trouvé : " + item.nodeId);
        }
    });
    node.warn("subscribe injectMssge : " + JSON.stringify(injectMssge, null, 2));

    node.send(injectMssge);  // Envoi du message après validation
}

// Si le message start est à true, on active l’acquisition
if (msg.start === true) {
    global.set("acquisitionActive", true);
    node.status({ fill: "green", shape: "dot", text: "Acquisition en cours..." });
}
// Si le message start est à false, on arrête l’acquisition
else if (msg.start === false) {
    global.set("acquisitionActive", false);
    node.status({ fill: "red", shape: "ring", text: "Acquisition arrêtée" });
}

return null;

//node.warn("msg spy " + JSON.stringify(msg, null, 2));

// Vérifie la structure du message
if (!msg.payload || !msg.payload.value || msg.payload.value.value === undefined) {
    node.warn("Message invalide ou incomplet");
    return null;
}

// Récupère le nodeId depuis addressSpaceItems
let nodeId = "unknownNode";
if (msg.addressSpaceItems && msg.addressSpaceItems.length > 0) {
    nodeId = msg.addressSpaceItems[0].nodeId;
}

// Récupère le nom mappé (depuis global context)
const nodeMap = global.get("NodeIdToName") || {};
const name = nodeMap[nodeId] || nodeId;

// Extraction des infos
const value = msg.payload.value.value;
const timestamp = new Date(msg.payload.sourceTimestamp || Date.now()).toISOString();
const dataType = msg.payload.value.dataType || "unknown";

// Construction du nouveau payload
msg.payload = {
    name: name,
    nodeId: nodeId,
    value: value,
    dataType: dataType,
    timestamp: timestamp
};

return msg;

let nodeId = msg.payload || msg.topic;  // Supporte les deux cas
let meta = flow.get(nodeId);

if (meta) {
    node.warn("Données trouvées : " + JSON.stringify(meta));
    msg.meta = meta;
} else {
    node.warn("Aucune donnée trouvée pour : " + nodeId);
    msg.meta = null;
}

return msg;

//Get global flag
var bConnectLost = global.get('ConnectionLost') || false;

//Default settings
var injectMssge = {};
injectMssge.topic="";
injectMssge.nodetype="inject";
injectMssge.injectType="listen";
injectMssge.addressSpaceItems=[];
//injectMssge.payload=1000;
var optionsParam = {
            "requestedPublishingInterval": 1000,
            //"requestedLifetimeCount": 99999,
            "requestedLifetimeCount": 10,
            "requestedMaxKeepAliveCount": 5,
            "maxNotificationsPerPublish": 10,
            "publishingEnabled": true,
            "priority": 10,
            "clientName":"nr-iot",
      };

injectMssge.payload={ "interval": 6000, "queueSize": 10, "options":optionsParam };

//Specific case if the configuration contains only one variable
if(Array.isArray(msg.varNameList) === false)
 {
    injectMssge.addressSpaceItems.push({"name": msg.varNameList, "nodeId":  msg.varNodeIdList}); 
 }
 else
 {
    //Add variables
    for(var iIndVar=0;iIndVar<msg.varNameList.length; iIndVar++)
    {
      injectMssge.addressSpaceItems.push( {"name": msg.varNameList[iIndVar], "nodeId":  msg.varNodeIdList[iIndVar]});
    }
 }

 if(bConnectLost === false)
 {
   console.log("---- FIRST SUBSCRIBE ------")
   SendSubscribeMessage();
 }
 else
 {
    //In case of connection lost, send another subscription
    console.log("---- CONNECT LOST - RESUBSCRIBE  ------")
    SendSubscribeMessage();
    setTimeout(SendSubscribeMessage, 5000);
  }

  //Send inject message
  function SendSubscribeMessage()
  {
    node.send(injectMssge);
  }

